import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import AccountBalance from './AccountBalance';

class Credits extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newCredit: {
        description: '',
        amount: '',
        // Store date as yyyy-mm-dd for consistency with display
        date: new Date().toISOString().slice(0, 10),
      },
    };
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    this.setState((prevState) => ({
      newCredit: {
        ...prevState.newCredit,
        [name]: value,
      },
    }));
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { amount } = this.state.newCredit;
    const newCredit = {
      ...this.state.newCredit,
      amount: parseFloat(amount),
    };
    this.props.addCredit(newCredit);
    this.setState({
      newCredit: {
        description: '',
        amount: '',
        date: new Date().toISOString().slice(0, 10),
      },
    });
  };

  render() {
    const { credits, accountBalance } = this.props;
    const { description, amount } = this.state.newCredit;

    return (
      <div>
        <h1>Credits</h1>
        <AccountBalance accountBalance={accountBalance} />
        <br />
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Description</label>
            <input
              type="text"
              name="description"
              value={description}
              onChange={this.handleChange}
            />
          </div>
          <div>
            <label>Amount</label>
            <input
              type="number"
              name="amount"
              value={amount}
              onChange={this.handleChange}
            />
          </div>
          <button type="submit">Add Credit</button>
        </form>
        <br />
        <div>
          {credits.map((credit, index) => (
            <div key={index}>
              <p>Description: {credit.description}</p>
              <p>Amount: {Number(credit.amount).toFixed(2)}</p>
              <p>Date: {String(credit.date).slice(0, 10)}</p>
            </div>
          ))}
        </div>
        <br />
        <Link to="/">Return to Home</Link>
      </div>
    );
  }
}

export default Credits;
